import React, { useState, useEffect } from "react";
import { BrowserProvider } from "ethers";
import { NETWORK_CONFIG } from "../constants/abis"; // âœ… Import network details

const supportedNetworks = {
  1: { chainId: "0x1", name: "Ethereum Mainnet" },
  56: { chainId: "0x38", name: "Binance Smart Chain" },
  137: { chainId: "0x89", name: "Polygon" }, // âœ… Ensure correct hex format
  80001: { chainId: "0x13881", name: "Polygon Mumbai" },
  97: { chainId: "0x61", name: "BSC Testnet" },
};

const switchNetwork = async (hexChainId) => {
  try {
    console.log(`ðŸ”„ Attempting to switch to chain: ${hexChainId}`);

    await window.ethereum.request({
      method: "wallet_switchEthereumChain",
      params: [{ chainId: hexChainId }], // âœ… MetaMask requires `0x` prefixed hex
    });

    console.log(`âœ… Successfully switched to network ${hexChainId}`);
  } catch (error) {
    console.error("âŒ Error switching network:", error);

    if (error.code === 4902) {
      alert("Network not added to MetaMask! Please add it manually.");
    }
  }
};

const NetworkSelector = ({ setSelectedNetwork }) => {
  return (
    <div className="text-center mt-4">
      <h5>Select Network:</h5>
      <select
        className="form-select w-50 mx-auto"
        onChange={(e) => switchNetwork(e.target.value)}
      >
        {Object.entries(supportedNetworks).map(([id, net]) => (
          <option key={id} value={net.chainId}>
            {net.name}
          </option>
        ))}
      </select>
    </div>
  );
};

export default NetworkSelector;
