import React from "react";
import { Button } from "@chakra-ui/react";
import { ethers } from "ethers"; // ✅ Corrected import

console.log("✅ WalletConnect.js file has loaded");

const WalletConnect = ({ setWallet }) => {  // ✅ Receive setWallet from App.js
  console.log("✅ WalletConnect component is rendering");

  const connectWallet = async () => {
    if (!window.ethereum) {
      alert("MetaMask is required.");
      return;
    }

    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum); // ✅ Corrected from BrowserProvider
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();
      const address = await signer.getAddress();

      setWallet(address);  // ✅ Set wallet globally in App.js
      console.log("✅ Wallet connected:", address);
    } catch (error) {
      console.error("❌ Connection error:", error);
    }
  };

  return (
    <Button colorScheme="teal" onClick={connectWallet}>
      Connect Wallet
    </Button>
  );
};

export default WalletConnect;
