import React, { useState } from "react";
import ReactDOM from "react-dom/client";

console.log("‚úÖ React is running");

// Directly defining the component here to eliminate import issues
const WalletConnect = () => {
  console.log("‚úÖ WalletConnect component is rendering");

  const [walletAddress, setWalletAddress] = useState(null);

  const connectWallet = async () => {
    if (!window.ethereum) {
      alert("MetaMask is required.");
      return;
    }

    try {
      const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
      setWalletAddress(accounts[0]);
      console.log("‚úÖ Wallet connected:", accounts[0]);
    } catch (error) {
      console.error("‚ùå Connection error:", error);
    }
  };

  return (
    <button
      onClick={connectWallet}
      style={{
        display: "block",
        margin: "20px auto",
        padding: "10px 20px",
        fontSize: "18px",
        cursor: "pointer",
        background: "teal",
        color: "white",
        border: "none",
        borderRadius: "5px",
      }}
    >
      {walletAddress ? `Connected: ${walletAddress.slice(0, 6)}...${walletAddress.slice(-4)}` : "Connect Wallet"}
    </button>
  );
};

const App = () => {
  console.log("‚úÖ App component is rendering");
  return (
    <div style={{ textAlign: "center", marginTop: "20vh" }}>
      <h1>üöÄ WalletConnect Test</h1>
      <WalletConnect />
    </div>
  );
};

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<App />);
