import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { useSelector } from "react-redux";
import { getERC20Approvals } from "../utils/erc20Approvals";

const ExistingApprovals = () => {
    const wallet = useSelector((state) => state.wallet);
    const provider = useSelector((state) => state.provider); // Ensure provider exists in Redux
    const [approvals, setApprovals] = useState([]);

    // ‚úÖ Defensive check: Ensure wallet and provider are valid
    if (!wallet || !wallet.address) {
        console.error("‚ùå Wallet address is missing in Redux state!");
        return <div>Error: Wallet not connected.</div>;
    }

    if (!provider) {
        console.error("‚ùå Provider is missing in Redux state!");
        return <div>Error: No provider found.</div>;
    }

    const fetchApprovals = async () => {
        try {
            console.log("‚úÖ Wallet Address:", wallet.address);

            // üîç Verify if provider is actually working
            console.log("üåê Using provider:", provider);

            const tokenContracts = [
                "0xTokenAddress1", // Replace with real addresses
                "0xTokenAddress2"
            ];

            const approvals = await getERC20Approvals(tokenContracts, wallet.address, provider);
            setApprovals(approvals);
        } catch (error) {
            console.error("‚ùå Error fetching approvals:", error);
        }
    };

    useEffect(() => {
        fetchApprovals();
    }, []);

    return (
        <div>
            <h2>Existing Approvals</h2>
            {approvals.length === 0 ? (
                <p>No approvals found.</p>
            ) : (
                <ul>
                    {approvals.map((approval, index) => (
                        <li key={index}>
                            {approval.contract} - Spender: {approval.spender} - Amount: {approval.amount}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ExistingApprovals;
