// ApprovalDashboard.js
import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchApprovals } from '../utils/erc20Approvals';

const ApprovalDashboard = () => {
    const [approvals, setApprovals] = useState([]);
    const account = useSelector((state) => state.web3.account);
    const dispatch = useDispatch();

    useEffect(() => {
        if (account) {
            fetchApprovals(account).then(setApprovals).catch(console.error);
        }
    }, [account, dispatch]);

    return (
        <div>
            <h2>Approval Dashboard</h2>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Token</th>
                        <th>Spender</th>
                        <th>Allowance</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {approvals.map((approval, index) => (
                        <tr key={index}>
                            <td>{approval.token}</td>
                            <td>{approval.spender}</td>
                            <td>{approval.allowance}</td>
                            <td>
                                <button className="btn btn-danger">Revoke</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ApprovalDashboard;
