import React, { useEffect, useState } from "react"; // Import React and hooks
import { getERC1155Approvals } from "../utils/erc1155Approvals"; // Importing getERC1155Approvals function

const ERC1155Approvals = ({ contractAddress, owner }) => {
    const [approvals, setApprovals] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchApprovals = async () => {
            try {
                if (!contractAddress || !owner) {
                    throw new Error("Missing contract address or owner address");
                }

                console.log("üîç Fetching ERC-1155 approvals for contract:", contractAddress);
                const approvalsData = await getERC1155Approvals(owner);
                setApprovals(approvalsData || []);
            } catch (err) {
                console.error("‚ùå Error fetching ERC-1155 approvals:", err.message);
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchApprovals();
    }, [contractAddress, owner]);

    return (
        <div>
            <h3>ERC-1155 Approvals</h3>
            {loading ? (
                <p>Loading approvals...</p>
            ) : error ? (
                <p style={{ color: "red" }}>Error: {error}</p>
            ) : approvals.length === 0 ? (
                <p>No approvals found.</p>
            ) : (
                <ul>
                    {approvals.map((approval, index) => (
                        <li key={index}>
                            Spender: {approval.spender} | Approved: {approval.isApproved ? "‚úÖ" : "‚ùå"}
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ERC1155Approvals;
