// Fresh copy of ExistingApprovals.js

import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { getERC20Approvals } from '../utils/erc20Approvals';
import { getERC721Approvals } from '../utils/nftApprovals';
import { getERC1155Approvals } from '../utils/erc1155Approvals';

const ExistingApprovals = () => {
    const [approvals, setApprovals] = useState([]);
    const account = useSelector(state => state.web3.account);

    useEffect(() => {
        if (!account) return;

        const fetchApprovals = async () => {
            try {
                const erc20Approvals = await getERC20Approvals(account);
                const erc721Approvals = await getERC721Approvals(account);
                const erc1155Approvals = await getERC1155Approvals(account);

                setApprovals([...erc20Approvals, ...erc721Approvals, ...erc1155Approvals]);
            } catch (error) {
                console.error('Error fetching approvals:', error);
            }
        };

        fetchApprovals();
    }, [account]);

    return (
        <div>
            <h2>Existing Approvals</h2>
            {approvals.length === 0 ? (
                <p>No approvals found.</p>
            ) : (
                <ul>
                    {approvals.map((approval, index) => (
                        <li key={index}>{approval.spender} - {approval.amount}</li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default ExistingApprovals;
