// NFTApprovals.js
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux';
import { getNFTApprovals } from '../utils/nftApprovals';

const NFTApprovals = () => {
    const [approvals, setApprovals] = useState([]);
    const account = useSelector((state) => state.web3.account);

    useEffect(() => {
        if (account) {
            getNFTApprovals(account).then(setApprovals).catch(console.error);
        }
    }, [account]);

    return (
        <div>
            <h2>NFT Approvals</h2>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Collection</th>
                        <th>Spender</th>
                        <th>Approved</th>
                    </tr>
                </thead>
                <tbody>
                    {approvals.map((approval, index) => (
                        <tr key={index}>
                            <td>{approval.collection}</td>
                            <td>{approval.spender}</td>
                            <td>{approval.approved ? 'Yes' : 'No'}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default NFTApprovals;
