console.log("‚úÖ App.js file has loaded");

const App = () => {
  console.log("‚úÖ App component is rendering");

  console.log("üîç Checking Redux state:");
  console.log("üîπ Wallet:", wallet);
  console.log("üîπ Selected Network:", selectedNetwork);
  console.log("üîπ Active Contracts:", activeContracts);

  return (
    <div className="container mt-5">
      {console.log("üîç UI is being rendered!")}

      <h1 className="text-center text-primary fw-bold">Mini Revoke Cash</h1>

      <WalletConnect setWallet={setWallet} />
      {console.log("üîç WalletConnect should be visible")}

      <NetworkSelector setSelectedNetwork={setSelectedNetwork} />
      {console.log("üîç NetworkSelector should be visible")}

      {wallet && (
        <>
          <ExistingApprovals wallet={wallet} />
          {console.log("üîç ExistingApprovals should be visible")}

          <ApprovalDashboard wallet={wallet} contractAddresses={activeContracts} />
          {console.log("üîç ApprovalDashboard should be visible")}
        </>
      )}

      <TokenAllowanceManager wallet={wallet} />
      {console.log("üîç TokenAllowanceManager should be visible")}

      {wallet && activeContracts?.erc721 && (
        <>
          <NFTApprovals contractAddress={activeContracts.erc721} />
          {console.log("üîç NFTApprovals should be visible")}
        </>
      )}

      {wallet && activeContracts?.erc1155 && (
        <>
          <ERC1155Approvals contractAddress={activeContracts.erc1155} />
          {console.log("üîç ERC1155Approvals should be visible")}
        </>
      )}

      {wallet && (
        <button
          className="btn btn-danger btn-lg fw-bold"
          onClick={() =>
            BatchRevoke({
              erc20Contracts: [activeContracts.tokenManager],
              erc721Contract: activeContracts.erc721,
              erc1155Contract: activeContracts.erc1155,
              signer: wallet,
            })
          }
        >
          üö® Revoke All Approvals
        </button>
      )}
    </div>
  );
};
