import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { useSelector } from "react-redux";
import { getERC20Approvals } from "../utils/erc20Approvals";

const ExistingApprovals = () => {
  const wallet = useSelector((state) => state.web3.account);
  const [approvals, setApprovals] = useState([]);

  useEffect(() => {
    if (wallet && window.ethereum) {
      fetchApprovals();
    }
  }, [wallet]);

  const fetchApprovals = async () => {
    try {
      console.log("üîç Fetching existing approvals...");

      if (!wallet || typeof wallet !== "string") {
        console.error("‚ùå Invalid wallet address:", wallet);
        return;
      }

      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();

      console.log("‚úÖ Wallet Address:", wallet);
      console.log("‚úÖ Provider:", provider);
      console.log("‚úÖ Signer:", signer);

      const erc20Approvals = await getERC20Approvals(wallet, signer, provider);

      setApprovals(erc20Approvals);
      console.log("‚úÖ ERC-20 Approvals:", erc20Approvals);
    } catch (error) {
      console.error("‚ùå Error fetching approvals:", error);
    }
  };

  return (
    <div>
      <h3>Existing Approvals</h3>
      {approvals.length > 0 ? (
        <ul>
          {approvals.map((approval, index) => (
            <li key={index}>{approval.spender}: {approval.amount}</li>
          ))}
        </ul>
      ) : (
        <p>No approvals found.</p>
      )}
    </div>
  );
};




export default ExistingApprovals;
