import { ethers, Contract } from "ethers";

/** Function to get ERC-20 Approvals */
export async function getERC20Approvals(tokenContracts, ownerAddress, provider) {
    const abi = ["function allowance(address owner, address spender) view returns (uint256)"];

    let approvals = [];

    // üöÄ Hardhat-compatible spender addresses (NO ENS!)
    const spenderAddresses = [
        "0x0000000000000000000000000000000000000000" // Dummy zero address to prevent ENS lookups
    ];

    for (let tokenAddress of tokenContracts) {
        const contract = new ethers.Contract(
  ethers.utils.getAddress(tokenAddress), // Ensure checksum format
  abi,
  signer
);


        for (let spender of spenderAddresses) {
            console.log(`üõ†Ô∏è Checking allowance for spender: ${spender}`);

            try {
                const allowance = await contract.allowance(ownerAddress, spender);
                if (allowance > 0) {
                    approvals.push({
                        contract: tokenAddress,
                        type: "ERC-20",
                        spender: spender,
                        amount: allowance.toString()
                    });
                }
            } catch (error) {
                console.error(`‚ùå Error fetching allowance for spender: ${spender}`, error);
            }
        }
    }

    console.log("‚úÖ ERC-20 Approvals Fetched:", approvals);
    return approvals;
}
