import React, { useEffect } from "react"; // Importing React and hooks
import { useSelector } from "react-redux"; // Importing useSelector for Redux state
import WalletConnect from "./components/WalletConnect.js"; // Importing components
import TokenAllowanceManager from "./components/TokenAllowanceManager.js";
import NFTApprovals from "./components/NFTApprovals.js";
import ERC1155Approvals from "./components/ERC1155Approvals.js"; 
import ApprovalDashboard from "./components/ApprovalDashboard.js";
import BatchRevoke from "./components/BatchRevoke.js";
import NetworkSelector from "./components/NetworkSelector.js";
import ExistingApprovals from "./components/ExistingApprovals.js";
import { CONTRACT_ADDRESSES } from "./constants/abis"; // Importing contract addresses
import "bootstrap/dist/css/bootstrap.min.css"; // Import styles from Bootstrap

console.log("‚úÖ App.js file has loaded");

const App = () => {
    console.log("‚úÖ App component is rendering");

    const wallet = useSelector((state) => state.web3.account);
    const network = useSelector((state) => state.web3.network);

    // Log Redux updates
    useEffect(() => {
        console.log("üîç Redux Network:", network);
        console.log("üîç Redux Account:", wallet);
    }, [network, wallet]);

    return (
        <div className="container mt-5">
            <h1 className="text-center text-primary fw-bold">Mini Revoke Cash</h1>
            <WalletConnect />
            <NetworkSelector />

            {wallet && network && (
                <>
                    <ExistingApprovals />  {/* Ensures ERC-20 approvals show up */}
                    <ApprovalDashboard />  {/* Fetches all approvals */}
                    <TokenAllowanceManager />  {/* Allows managing token allowances */}
                    <NFTApprovals contractAddress={CONTRACT_ADDRESSES.TestNFT} />  {/* Ensures NFT approvals load */}
                    <ERC1155Approvals contractAddress={CONTRACT_ADDRESSES.ERC1155} owner={wallet} />  {/* Loads ERC-1155 approvals */}
                </>
            )}

            {wallet && (
                <button className="btn btn-danger btn-lg fw-bold">
                    üö® Revoke All Approvals
                </button>
            )}
        </div>
    );
};

export default App;
