import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { getERC20Approvals } from "../utils/erc20Approvals";
import { getERC721Approvals } from "../utils/nftApprovals";
import { getERC1155Approvals } from "../utils/erc1155Approvals";
import { ethers } from "ethers";

const ExistingApprovals = () => {
  const wallet = useSelector((state) => state.web3.account);
  const network = useSelector((state) => state.web3.network);
  const [approvals, setApprovals] = useState({ erc20: [], erc721: [], erc1155: [] });

  useEffect(() => {
    if (wallet && window.ethereum) {
      fetchApprovals();
    }
  }, [wallet, network]);

  const fetchApprovals = async () => {
    try {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();

      const erc20Approvals = await getERC20Approvals(wallet, signer);
      const erc721Approvals = await getERC721Approvals(wallet, signer);
      const erc1155Approvals = await getERC1155Approvals(wallet, signer);

      setApprovals({ erc20: erc20Approvals, erc721: erc721Approvals, erc1155: erc1155Approvals });
    } catch (error) {
      console.error("❌ Error fetching approvals:", error);
    }
  };

  return (
    <div className="container mt-4">
      <h3>Existing Token Approvals</h3>
      <p><strong>Wallet:</strong> {wallet || "Not Connected"}</p>

      {/* ERC-20 Approvals */}
      <div className="card p-3 mb-3">
        <h5>ERC-20 Approvals</h5>
        {approvals.erc20.length > 0 ? (
          <table className="table">
            <thead>
              <tr>
                <th>Token</th>
                <th>Spender</th>
                <th>Allowance</th>
              </tr>
            </thead>
            <tbody>
              {approvals.erc20.map((approval, index) => (
                <tr key={index}>
                  <td>{approval.token}</td>
                  <td>{approval.spender}</td>
                  <td>{approval.amount}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No ERC-20 approvals found.</p>
        )}
      </div>
    </div>
  );
};

export default ExistingApprovals;
