import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { useSelector } from "react-redux";
import { getERC20Approvals } from "../utils/erc20Approvals";

const ExistingApprovals = () => {
  const wallet = useSelector((state) => state.web3.account);
  const [approvals, setApprovals] = useState([]);

  useEffect(() => {
    if (wallet && window.ethereum) {
      fetchApprovals();
    }
  }, [wallet]);

const fetchApprovals = async () => {
  try {
    console.log("üîç Testing contract interaction...");

    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    const walletAddress = await signer.getAddress();

    console.log("‚úÖ Wallet Address from Redux:", wallet);
    console.log("‚úÖ Resolved Signer Address:", walletAddress);
    console.log("‚úÖ Provider:", provider);

    // TEMPORARY TEST FUNCTION TO ISOLATE ERROR
    const testContract = new ethers.Contract(
      walletAddress, // We use the wallet address as a test (should fail safely)
      [],            // Empty ABI (we're not calling a function yet)
      provider
    );

    console.log("‚úÖ Test Contract Instance Created:", testContract);

  } catch (error) {
    console.error("‚ùå Error testing contract:", error);
  }
};


  return (
    <div>
      <h3>Existing Approvals</h3>
      {approvals.length > 0 ? (
        <ul>
          {approvals.map((approval, index) => (
            <li key={index}>{approval.spender}: {approval.amount}</li>
          ))}
        </ul>
      ) : (
        <p>No approvals found.</p>
      )}
    </div>
  );
};

export default ExistingApprovals;
