import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { useSelector } from "react-redux";
import { getERC20Approvals } from "../utils/erc20Approvals";

const ExistingApprovals = () => {
  const wallet = useSelector((state) => state.web3.account);
  const [approvals, setApprovals] = useState([]);

  useEffect(() => {
    if (wallet && window.ethereum) {
      fetchApprovals();
    }
  }, [wallet]);

  const fetchApprovals = async () => {
    try {
      console.log("üîç Fetching ERC-20 approvals...");

      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.ready; // Ensure provider has loaded network data
      const signer = provider.getSigner();
      const walletAddress = await signer.getAddress();

      console.log("‚úÖ Wallet Address from Redux:", wallet);
      console.log("‚úÖ Resolved Signer Address:", walletAddress);
      console.log("‚úÖ Provider:", provider);

      console.log("üåç Checking network...");
      const network = await provider.getNetwork();
      console.log("‚úÖ Network detected:", network);

      if (!network || network.chainId === undefined) {
        throw new Error("‚ùå Provider failed to detect network.");
      }

      const address = ethers.utils.getAddress(walletAddress); // Ensure it's a checksum address
      console.log("‚úÖ Checksum Wallet Address:", address);

      const signerAddress = await signer.getAddress();
      console.log("üö® Debug: Parameters being passed:");
      console.log("walletAddress:", walletAddress);
      console.log("signerAddress:", signerAddress);
      console.log("provider:", provider);

      const erc20Approvals = await getERC20Approvals(walletAddress, signerAddress, provider);
      setApprovals(erc20Approvals);
      console.log("‚úÖ ERC-20 Approvals:", erc20Approvals);
    } catch (error) {
      console.error("‚ùå Error fetching approvals:", error);
    }
  };

  return (
    <div>
      <h3>Existing ERC-20 Approvals</h3>
      {approvals.length > 0 ? (
        <ul>
          {approvals.map((approval, index) => (
            <li key={index}>
              <strong>Spender:</strong> {approval.spender} | <strong>Amount:</strong> {approval.amount}
            </li>
          ))}
        </ul>
      ) : (
        <p>No approvals found.</p>
      )}
    </div>
  );
};

export default ExistingApprovals;
