import React, { useState, useEffect } from "react";
import { BrowserProvider } from "ethers";
import { NETWORK_CONFIG } from "../constants/abis"; // ✅ Import network details

const NetworkSelector = ({ setSelectedNetwork }) => {
  const [currentNetwork, setCurrentNetwork] = useState(null);

  useEffect(() => {
    const detectNetwork = async () => {
      if (window.ethereum) {
        const provider = new BrowserProvider(window.ethereum);
        const network = await provider.getNetwork();
        setCurrentNetwork(network.chainId);
        setSelectedNetwork(network.chainId);
      }
    };

    detectNetwork();

    window.ethereum.on("chainChanged", () => {
      window.location.reload(); // Refresh on network change
    });
  }, [setSelectedNetwork]);

const supportedNetworks = {
  1: { chainId: "0x1", name: "Ethereum Mainnet" },
  56: { chainId: "0x38", name: "Binance Smart Chain" },
  137: { chainId: "0x89", name: "Polygon" }, // ✅ Use hex format
  80001: { chainId: "0x13881", name: "Polygon Mumbai" },
  97: { chainId: "0x61", name: "BSC Testnet" },
};

const switchNetwork = async (chainId) => {
  try {
    await window.ethereum.request({
      method: "wallet_switchEthereumChain",
      params: [{ chainId }], // ✅ Ensure proper format
    });
  } catch (error) {
    if (error.code === 4902) {
      alert("Network not added to MetaMask! Please add it manually.");
    } else {
      console.error("❌ Error switching network:", error);
    }
  }
};


  return (
    <div className="text-center mt-4">
      <h5>Select Network:</h5>
      <select
        className="form-select w-50 mx-auto"
        onChange={(e) => switchNetwork(e.target.value)}
      >
        {Object.entries(NETWORK_CONFIG).map(([id, net]) => (
          <option key={id} value={net.chainId}>
            {net.name}
          </option>
        ))}
      </select>
      <p className="fw-bold mt-2">
        Current Network: {NETWORK_CONFIG[currentNetwork]?.name || "Unknown"}
      </p>
    </div>
  );
};

export default NetworkSelector;
